const apply = (event) => {


  for (var i = 0; i <= jobsData.length - 1; i++) {

    document.getElementById(
      "job-form"
    ).innerHTML = `
                  <h3 class="text-xl font-medium text-center text-gray-900 dark:text-white">Apply using Resume Link</h3>
                  <div>
                      <label for="Name" class="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Your Name</label>
                      <input type="text" name="Name" id="Name" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white" placeholder="My name is Khan" required>
                  </div>
                  <div>
                    <label for="jobId" class="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Job ID</label>
                    <input type="tel" name="jobId" id="jobId" value=${event.target.id}  placeholder="10001" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white" required>
             
                </div>
                  <div>
                      <label for="link" class="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Resume Link</label>
                      <input type="text" name="link" id="link"  placeholder="www.linkedin.com/khan_resume" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white" required>
                  </div>
                  
                  <button type="submit" class="w-full text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800" > Apply </button>
                  
              `;
  }
};

